__NUXT_JSONP__("/gallery/one-of-our-kittens-of-years-ago-oj-gazing-into-a", (function(a,b){return {data:[{document:{date:a,title:[{type:"heading3",text:"Mirror, Mirror",spans:[]}],photo:{dimensions:{width:800,height:1200},alt:a,copyright:a,url:"https:\u002F\u002Fimages.prismic.io\u002Ffunk\u002F31ac74e0-1474-4385-9664-6ac0e24d7a8a_MirrorMirror.jpg?auto=compress,format",small:{dimensions:{width:320,height:480},alt:a,copyright:a,url:"https:\u002F\u002Fimages.prismic.io\u002Ffunk\u002F31ac74e0-1474-4385-9664-6ac0e24d7a8a_MirrorMirror.jpg?auto=compress,format&rect=0,0,800,1200&w=320&h=480"}},content:[{type:b,text:"One of our kittens of years ago, OJ, gazing into a make-up  mirror, watched over by curious cockatiel. The bird was photographed in a  local pet shop and added with CS3, as was its shadow, the blue color on  the wall and an additional shot of the picture on the wall to cover the  original. Other edits included level adjustments and a touch of soft  focus.\nI had to wait till OJ got totally tired before he’d sit still… in the end I had to snap my fingers to get him to open his eyes!",spans:[]},{type:b,text:"All  elements were shot on Fuji Reala 100 ISO color print film on a Canon  EOS-100 SLR. The negatives were scanned to digital on a Canon FS 2710  film scanner",spans:[]}],page:"Page 1",tag:"pets montage"}}],fetch:[],mutations:void 0}}(null,"paragraph")));